mapper.delete:
delete from wwd_activity_order where id in
#{item}
and del_flag = 'N'

mapper.update:
update wwd_activity_order set DEL_FLAG = 'Y' where id in
#{item}
and del_flag = 'N'

update wwd_activity_order set DEL_FLAG = 'Y' , UPDATE_AT = now() , UPDATE_BY = #{userId} where id in
#{item}
and del_flag = 'N'


