mapper.select:

select

from wwd_activity_order
where del_flag = 'N'

  and order_no = #{orderNo,jdbcType=VARCHAR}


  and activity_title = #{activityTitle,jdbcType=VARCHAR}


  and activity_url = #{activityUrl,jdbcType=VARCHAR}


  and participate_id = #{participateId,jdbcType=VARCHAR}


  and user_id = #{userId,jdbcType=VARCHAR}


  and pay_type = #{payType,jdbcType=VARCHAR}


  and pay_status = #{payStatus,jdbcType=CHAR}


  and remarks = #{remarks,jdbcType=VARCHAR}


  and DATA_USER_ID = #{dataUserId,jdbcType=VARCHAR}


  and DATA_OFFICE_ID = #{dataOfficeId,jdbcType=VARCHAR}


  and DATA_TYPE = #{dataType,jdbcType=VARCHAR}


  and DATA_AREA_ID = #{dataAreaId,jdbcType=VARCHAR}


  and DEL_FLAG = #{delFlag,jdbcType=CHAR}


  and CREATE_AT = #{createAt,jdbcType=TIMESTAMP}


  and CREATE_BY = #{createBy,jdbcType=VARCHAR}


  and UPDATE_AT = #{updateAt,jdbcType=TIMESTAMP}


  and UPDATE_BY = #{updateBy,jdbcType=VARCHAR}


  and (activity_title like CONCAT('%',#{keyword},'%')
  or  pay_type like CONCAT('%',#{keyword},'%')
  or  order_no like CONCAT('%',#{keyword},'%')
  or  remarks like CONCAT('%',#{keyword},'%'))
