mapper.select:

select 

from wwd_banner
 where del_flag = 'N'


  and  title like CONCAT('%',#{entity.title},'%')


  and type = #{entity.type,jdbcType=VARCHAR}


  and  status = #{entity.status,jdbcType=VARCHAR}
