mapper.delete:
delete from wwd_user_param_qrcode where ID in
#{item}
and del_flag = 'N'

mapper.update:
update wwd_user_param_qrcode set DEL_FLAG = 'Y' where ID in
#{item}
and del_flag = 'N'

update wwd_user_param_qrcode set DEL_FLAG = 'Y' , UPDATE_AT = now() , UPDATE_BY = #{userId} where ID in
#{item}
and del_flag = 'N'


update wwd_user_param_qrcode
set DEL_FLAG =  'Y' 



       and ID = #{id,jdbcType=VARCHAR}


       and wwd_user_id = #{wwdUserId,jdbcType=VARCHAR}


       and ticket = #{ticket,jdbcType=VARCHAR}


       and expire_seconds = #{expireSeconds,jdbcType=INTEGER}


       and content = #{content,jdbcType=VARCHAR}


       and url = #{url,jdbcType=VARCHAR}


       and is_limit = #{isLimit,jdbcType=VARCHAR}


       and DATA_USER_ID = #{dataUserId,jdbcType=VARCHAR}


       and DATA_OFFICE_ID = #{dataOfficeId,jdbcType=VARCHAR}


       and DATA_TYPE = #{dataType,jdbcType=VARCHAR}


       and DATA_AREA_ID = #{dataAreaId,jdbcType=VARCHAR}


       and DEL_FLAG = #{delFlag,jdbcType=CHAR}


       and CREATE_AT = #{createAt,jdbcType=TIMESTAMP}


       and CREATE_BY = #{createBy,jdbcType=VARCHAR}


       and UPDATE_AT = #{updateAt,jdbcType=TIMESTAMP}


       and UPDATE_BY = #{updateBy,jdbcType=VARCHAR}


