mapper.select:

select

from
wwd_user wu
LEFT JOIN wwd_user_area wua ON wu.user_id = wua.wwd_user_id
where wu.del_flag = 'N' and wu.isverified = 'Y' and wu.show_in_list = 'Y'

  and (wu.name like concat('%',#{keyword},'%')
  or wu.nickname like concat('%',#{keyword},'%')
  or wua.now_province_name like concat('%',#{keyword},'%')
  or wua.now_city_name like concat('%',#{keyword},'%')
  or wua.now_district_name like concat('%',#{keyword},'%')
  or wua.home_province_name like concat('%',#{keyword},'%')
  or wua.home_city_name like concat('%',#{keyword},'%')
  or wua.home_district_name like concat('%',#{keyword},'%')
  )


  and wu.name like concat('%',#{name},'%')


  and wu.nickname like concat('%',#{nickname},'%')


  and wu.mobile like concat('%',#{mobile},'%')


  and wu.gender = #{gender}


  and wu.education = #{education}


  and wu.constellation = #{constellation}


  and wu.birth_day BETWEEN

    #{item}




  and (wua.home_province_id in

    #{item}

  or  wua.home_city_id in

    #{item}

  or wua.home_district_id in

    #{item}

  )



  and (wua.now_province_id in

    #{item}

  or  wua.now_city_id in

    #{item}

  or wua.now_district_id in

    #{item}

  )

ORDER BY wu.create_at desc
